import { Component } from "react";
import { Todo } from "./Todo";
import styles from "./List.module.css";


export class List extends Component {
  render() {

     const {todos} = this.props;
    return (
      <div className="list">
        {/* Render the todo here from the props*/}
      {todos.map( (todo,i) => 
      (
        <div key={i}>
         <h1>{todo.text}</h1> 
         <img  className={styles.img}  src="
         https://cdn-icons-png.flaticon.com/128/1828/1828843.png" onClick={() => this.props.handleRemove(i)}></img>
         <button className={styles.removeBt} >remove</button>
        </div>

      )  )}
       

      </div>
    );
  }
}
