import { Todo } from "./Todo";

export class List extends Component {
  render() {

     const {todos} = this.props;
    return (
      <div className="list">
        {/* Render the todo here from the props*/}
      {todos.map( (todo,i) => 
      (
        <div>
         <h1 key={i}>{todo.text}</h1> 
         <button>remove</button>
        </div>
     
      

      )  )}
       

      </div>
    );
  }
}
